// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Registration.sol";

contract Donation {
    Registration private registration;

    uint256 public totalDonations;
    uint256 public totalDonationAmount;

    event DonationReceived(address indexed donor, uint256 amount);

    constructor(address _registrationAddress) {
        registration = Registration(_registrationAddress);
    }

    function donate() public payable {
        require(msg.value > 0, "Donation amount must be greater than zero");

        totalDonations++;
        totalDonationAmount += msg.value;

        emit DonationReceived(msg.sender, msg.value);
    }

    function getTotalDonations() public view returns (uint256) {
        return totalDonations;
    }

    function getTotalDonationAmount() public view returns (uint256) {
        return totalDonationAmount;
    }
}
