// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Registration.sol";

contract Scholarship {
    Registration private registration;

    struct ScholarshipApplication {
        address studentAddress;
        uint256 amountRequested;
        string ipfsHash;
    }

    ScholarshipApplication[] public scholarshipApplications;
    uint256 public totalApplicationAmount;

    event ScholarshipApplied(address indexed studentAddress, uint256 amountRequested, string ipfsHash);

    constructor(address _registrationAddress) {
        registration = Registration(_registrationAddress);
    }

    function applyForScholarship(uint256 _amountRequested, string memory _ipfsHash) public {
        Registration.User memory user = registration.getUser(msg.sender);
        require(user.isRegistered, "User is not registered");
        require(keccak256(abi.encodePacked(user.userType)) == keccak256("Student"), "Only students can apply for scholarships");

        scholarshipApplications.push(ScholarshipApplication({
            studentAddress: msg.sender,
            amountRequested: _amountRequested,
            ipfsHash: _ipfsHash
        }));

        totalApplicationAmount += _amountRequested;
        registration.incrementTotalApplicationAmount(_amountRequested);
        emit ScholarshipApplied(msg.sender, _amountRequested, _ipfsHash);
    }

    function getTotalScholarshipApplications() public view returns (uint256) {
        return scholarshipApplications.length;
    }

    function getTotalScholarshipAmount() public view returns (uint256) {
        return totalApplicationAmount;
    }
}
